import { defaultValidate } from './utils';
var files = [{
  name: 'video.mkv'
}];
describe('Upload utils', function () {
  describe('defaultValidate', function () {
    it('Should return result with status error', function () {
      expect(defaultValidate(null)).toEqual({
        message: 'Загрузите файл',
        status: 'error',
        data: null
      });
    });
    it('Should return result with file name', function () {
      expect(defaultValidate(files)).toEqual({
        data: {
          name: 'video.mkv'
        }
      });
    });
    it('Should return result with file name by accept format', function () {
      expect(defaultValidate(files, '.mkv')).toEqual({
        data: {
          name: 'video.mkv'
        }
      });
    });
    it('Should return result with file name by accepts format', function () {
      expect(defaultValidate(files, '.avi, .mkv')).toEqual({
        data: {
          name: 'video.mkv'
        }
      });
    });
    it('Should return result with status error and message with accept format', function () {
      expect(defaultValidate(files, '.avi')).toEqual({
        data: null,
        message: 'Неверный формат файла. Используйте avi-формат',
        status: 'error'
      });
    });
  });
});